import React from 'react';
import { Switch, Route} from 'react-router-dom'

import GlobalFeed from 'pages/global-feed'
import Article from 'pages/article'
import Authentication from 'pages/authentication'
import TagFeed from 'pages/tag-feed'
import YourFeed from 'pages/your-feed'
import CreateArticle from 'pages/create-article'
import EditArticle from 'pages/edit-article'
import Settings from 'pages/settings'
import UserProfile from 'pages/user-profile'

export default () =>{
    return (
        <Switch>
            <Route path='/' exact component={GlobalFeed} />
            <Route path='/profiles/:slug' component={UserProfile} exact />
            <Route path='/profiles/:slug/favorites' component={UserProfile} />
            <Route path='/settings' component={Settings} />
            <Route path='/feed' component={YourFeed} />
            <Route path='/articles/new' component={CreateArticle} exact />
            <Route path='/articles/:slug/edit' component={EditArticle} />
            <Route path='/tags/:slug' component={TagFeed} />
            <Route path='/articles/:slug' component={Article} exact/>
            <Route path='/login' component={Authentication} />
            <Route path='/register' component={Authentication} />
        </Switch>
    )
}